mapper.select:
SELECT	CONFIG_SEQ
FROM	BOARD_CONFIG
WHERE	CONFIG_SEQ = #{configSeq}

mapper.insert:
INSERT INTO BOARD_ARTCLE (
	CONFIG_SEQ, TITLE, CONTENT, WRITER, REG_DATE, EDIT_DATE, VIEW_CO, IP, THUMB_ORIGIN_NM, THUMB_CHANGE_NM, THUMB_PATH, 
	TRASH_YN, ADD_ITEM01, ADD_ITEM02, ADD_ITEM03, ADD_ITEM04, ADD_ITEM05
) VALUES (
	#{configSeq}, #{title}, #{content}, #{writer}, now(), now(), 0, #{ip},
	
		
			null, null, null,
		
		
			#{thumbOriginNm}, #{thumnChangeNm}, #{thumbPath},
		
	
	'N', #{addItem01}, #{addItem02}, #{addItem03}, #{addItem04}, #{addItem05}
)

INSERT INTO BOARD_CONFIG (
	BOARD_NM, BOARD_USE_YN, COMMENT_USE_YN, ANONYMOUS_USE_YN, SECRET_USE_YN, REG_DATE, EDIT_DATE
) VALUES (
	#{boardNm}, #{boardUseYn}, #{commentUseYn}, #{anonymousUseYn}, #{secretUseYn}, now(), now()
)

mapper.select:
SELECT	CONFIG_SEQ, BOARD_NM, BOARD_USE_YN, COMMENT_USE_YN, EDITOR_USE_YN, ANONYMOUS_USE_YN, SECRET_USE_YN, REG_DATE, EDIT_DATE
FROM	BOARD_CONFIG
WHERE	CONFIG_SEQ = #{configSeq}
